import { makeStyles } from "@mui/styles";
import { Box } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import { Ranking, Suit } from "../../models/types";
import FmdBadIcon from '@mui/icons-material/FmdBad';
import ApiIcon from '@mui/icons-material/Api';
import AcUnitIcon from '@mui/icons-material/AcUnit';

const useStyles = makeStyles({
    box: {
        width: 50,
        height: 70,
        border: "1px solid black",
        backgroundColor: "white"
    },
    cardInfo: {
        display: "flex",
        margin: "auto",
        width: "max-content",
        alignItems: "center",
        gap: 1,
    }
});

interface PlayingCardProps {
    suit: Suit;
    ranking: Ranking;
}

const PlayingCard = (props: PlayingCardProps) => {
    const classes = useStyles();
    const suit = props.suit;
    const ranking = props.ranking;

    const showCard = () => {
        console.log(ranking)
        console.log(suit)
        let color = "";
        let suitNode = undefined;
        switch(suit.label){
            case "H":
                color = "red";
                suitNode = <FavoriteIcon htmlColor={color}/>
                break;
            case "D":
                color = "red";
                suitNode = <ApiIcon htmlColor={color}/>
                break;
            case "C":
                color = "black";
                suitNode = <AcUnitIcon htmlColor={color}/>
                break;
            case "S":
                color = "black";
                suitNode = <FmdBadIcon htmlColor={color}/>
                break;
        }
        let rankingNode = <span style={{color: color, fontWeight: "bold"}}>{ranking.value}</span>
        return <>{rankingNode}{suitNode}</>
    }

    return (
        <Box role="playing-card" className={classes.box}>
            <Box className={classes.cardInfo}>
                {showCard()}
            </Box>
        </Box>
    );
}

export default PlayingCard;
