import { makeStyles } from "@mui/styles";
import { Box, Button, CircularProgress, Container, Divider, Snackbar, Typography} from "@mui/material";
import FunctionsIcon from '@mui/icons-material/Functions';
import Title from "../components/Title";
import BasicCard from "../components/MontecarloPokerSimulation/BasicCard";
import { useState } from "react";
import { GameCards, MontecarloPokerSimulationSettings } from "../models/types";
import { fetchDrawCards, fetchShuffleDeck } from "../services/APIService";
import Alert from "../components/Alert";
import PlayingCard from "../components/MontecarloPokerSimulation/PlayingCard";

const useStyles = makeStyles({
  main: {
    display: "flex !important",
    flexDirection: "column",
    alignItems: "center",
    gap: 15,
  },
  box: {
    display: "flex",
    width: "70%",
    alignItems: "center",
    justifyContent: "space-evenly"
  },
  boxButton:{
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: 20,
    minHeight: 150,
    minWidth: 150,
  },
  logo: {
    maxWidth: 150,
  },
  backCards: {
    maxWidth: 150,
  },
  boxSettingsInfo: {
    display: "flex",
    flexDirection: "column"
  },
  containerTable:{
    padding: "0px 150px !important",
  },
  boxTable: {
    width: "100%",
    minHeight: 400,
    backgroundRepeat: "no-repeat",
    backgroundSize: "contain",
    backgroundPosition: "center",
  }
});

const MontecarloPokerSimulation = () => {
  const classes = useStyles();

  const [settings, setSettings] = useState<MontecarloPokerSimulationSettings>({nPlayers: 1, nSamples: 10000});
  const [gameCards, setGameCards] = useState<GameCards | undefined>();
  const [responseLoading, setResponseLoading] = useState(false);
  const [openAlert, setOpenAlert] = useState(false);

  const handleClick = () => {
    setOpenAlert(true);
  };

  const handleCloseAlert = (event?: React.SyntheticEvent | Event, reason?: string) => {
    if (reason === 'clickaway') {
      return;
    }
    setOpenAlert(false);
  };

  const setMontecarloPokerSimulationSettings = (settings: MontecarloPokerSimulationSettings) => {
    setSettings(settings);
  }

  const getDrawCards = () => {
    setResponseLoading(true);
    fetchDrawCards(settings.nPlayers)
      .then((res) => {

        setGameCards(res.body);
        setResponseLoading(false);
      })
  }

  const shuffleDeck = () => {
    fetchShuffleDeck()
      .then((res) => {
        setOpenAlert(true);
        setGameCards(undefined);
      })
  }

  return (
    <Container className={classes.main} maxWidth={"xl"}>
      <Title title={"Montecarlo - Poker Simulation"} icon={<img alt="Poker Logo" src={require("../assets/images/pokerLogo.jpg")} className={classes.logo}/>} />
      <BasicCard setSettings={setMontecarloPokerSimulationSettings}/>
      <Box className={classes.box}>
        {/* <img alt="Playing Cards Back" src={require("../assets/images/playingCardsBack.png")} className={classes.backCards} /> */}
        <Box className={classes.boxSettingsInfo}>
          <Typography sx={{ fontSize: 15, margin: 0 }} color="text.secondary" gutterBottom>
            N° Samples: {settings.nSamples}
          </Typography>
          <Typography sx={{ fontSize: 15, margin: 0 }} color="text.secondary" gutterBottom>
            N° Players: {settings.nPlayers}
          </Typography>
        </Box>
        <Box className={classes.boxButton}>
          <Button onClick={() => getDrawCards()} variant="contained" component="label">
            Draw Cards
          </Button>
          <Button onClick={() => shuffleDeck()} variant="outlined" component="label">
            Shuffle Deck
          </Button>
          <Snackbar open={openAlert} autoHideDuration={6000} onClose={handleCloseAlert}>
            <Alert onClose={handleCloseAlert} severity="success" sx={{ width: '100%' }}>
              Deck shuffled correctly!
            </Alert>
          </Snackbar>
        </Box>
        <Box className={classes.boxButton}>
          {gameCards && 
            <Box>
              <Typography sx={{ fontSize: 18, margin: 0 }} color="text.secondary" gutterBottom>
                Result:
              </Typography>
              <Typography sx={{ fontSize: 18, margin: 0 }} color="text.secondary" gutterBottom>
                <span color="green">Win: <b>{}</b></span> <span color="grey">Spare: <b>{}</b></span><span color="red">Lose: <b>{}</b></span>
              </Typography>
            </Box>
          }
        </Box>
      </Box>
      {responseLoading && 
        <CircularProgress />
      }
      {gameCards && 
        <Container className={classes.containerTable}>
          <Box className={classes.boxTable} sx={{backgroundImage: `url(${require("../assets/images/poker-table.jpeg")})`}}>
            <PlayingCard suit={gameCards.mainPlayerCards.cards[0].suit} ranking={gameCards.mainPlayerCards.cards[0].ranking} />
          </Box>
        </Container>
      }
    </Container>
  )
}

export default MontecarloPokerSimulation;
