import { makeStyles } from "@mui/styles";
import { Box, Button, CircularProgress, Container, Divider, Typography} from "@mui/material";
import FunctionsIcon from '@mui/icons-material/Functions';
import Title from "../components/Title";
import BasicCard from "../components/MontecarloPokerSimulation/BasicCard";
import { useState } from "react";
import { GameCards, MontecarloPokerSimulationSettings } from "../models/types";
import { fetchDrawCards } from "../services/APIService";

const useStyles = makeStyles({
  main: {
    display: "flex !important",
    flexDirection: "column",
    alignItems: "center",
    gap: 15,
  },
  box: {
    display: "flex",
    width: "100%"
  },
  logo: {
    maxWidth: 150,
  },
  backCards: {
    maxWidth: 150,
  },
  boxSettingsInfo: {
    display: "flex",
    flexDirection: "column"
  }
});

const MontecarloPokerSimulation = () => {
  const classes = useStyles();

  const [settings, setSettings] = useState<MontecarloPokerSimulationSettings>({nPlayers: 1, nSamples: 10000});
  const [gameCards, setGameCards] = useState<GameCards | undefined>();
  const [responseLoading, setResponseLoading] = useState(false);


  const setMontecarloPokerSimulationSettings = (settings: MontecarloPokerSimulationSettings) => {
    setSettings(settings);
  }

  const getDrawCards = () => {
    setResponseLoading(true);
    fetchDrawCards()
      .then((res) => {
        setGameCards(res);
        setResponseLoading(false);
      })
  }

  return (
    <Container className={classes.main} maxWidth={"xl"}>
      <Title title={"Montecarlo - Poker Simulation"} icon={<img alt="Poker Logo" src={require("../assets/images/pokerLogo.jpg")} className={classes.logo}/>} />
      <BasicCard setSettings={setMontecarloPokerSimulationSettings}/>
      <Box className={classes.box}>
        <img alt="Playing Cards Back" src={require("../assets/images/playingCardsBack.png")} className={classes.backCards} />
        <Box className={classes.boxSettingsInfo}>
          <Typography sx={{ fontSize: 15, margin: 0 }} color="text.secondary" gutterBottom>
            N° Samples: {settings.nSamples}
          </Typography>
          <Typography sx={{ fontSize: 15, margin: 0 }} color="text.secondary" gutterBottom>
            N° Players: {settings.nPlayers}
          </Typography>
        </Box>
        <Button onClick={getDrawCards} variant="contained">
          Draw Cards
        </Button>
      </Box>
      {responseLoading && 
        <CircularProgress />
      }
      {gameCards && 
        <Box>
          {gameCards.mainPlayerCards.cards[0].ranking}-{gameCards.mainPlayerCards.cards[0].suit}<br/>
          {gameCards.otherPlayerCards[0].cards[0].ranking}-{gameCards.otherPlayerCards[0].cards[0].suit}
        </Box>
      }
    </Container>
  )
}

export default MontecarloPokerSimulation;
