import Card from '@mui/material/Card';
import { useEffect, useState } from "react";
import { Box, CardActionArea, CardActions, CardContent, CardMedia, CircularProgress, Typography } from "@mui/material";
import { Dataset, DatasetResponse } from '../../models/types'
import { fetchObjectDetectionLoadDataset } from '../../services/APIService'
import classnames from "classnames";
import { MontecarloPokerSimulationSettings } from "../../utils/Model";

const useStyles = makeStyles({
    cardBox:{
        position: "absolute",
        zIndex: 1000,
        top: 30,
        right: 30,
    },
    card: {
        cursor: "pointer",
        minWidth: 200,
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        border: "1px solid #f5f5f5",
        boxShadow: "-4px 4px 4px -4px grey !important",
        "&:hover":{
            backgroundColor: "#f3f3f3",
        },
        "& > .MuiCardActions-root":{
            minWidth: 200,
        }
    },
    cardContent: {
    },
    cardActions:{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: 15
    },
    cardDetails:{
        display: "flex",
        flexDirection: "column",
        alignItems: "flex-start",
        width: "85%",
        gap: 10
    },
    cardLoading:{
        opacity: 0.3
    },
    divider: {
        width: "100%",
        margin: "20px 0px !important"
    },
    cardOpen:{
        cursor: "auto",
        minWidth: 450,
        boxShadow: "-2px 2px 2px -2px grey !important",
        "&:hover":{
            backgroundColor: "white",
            "& > div > p, & > div > div":{
                color: "text.primary !important"
            }
        }
    },
    loadingButton:{
        position: "absolute",
        top: "40%",
        left: "40%",
        zIndex: 1202
    }
})

interface CardProps{
    setSettings: Function
}

const BasicCard = (props: CardProps) => {
    const classes = useStyles();

    const [cardOpen, setCardOpen] = useState(false);
    const [expandDatasetDetails, setExpandDatasetDetails] = useState(false);

    const handleClose = () => {
        setSettings()
    };

    const handleCloseCard = (e:any) => {
        setCardOpen(!cardOpen)
    }

    const handleExpandClick = () => {
        setExpandDatasetDetails(!expandDatasetDetails);
    };


    return (
        <Box className={classes.cardBox}>
            {!cardOpen &&
                <Card className={classnames(classes.card, cardOpen ? classes.cardOpen : "")} onClick={(event) => handleCloseCard(event)}>
                    <CardContent>
                        <Typography sx={{ fontSize: 15 }} color="text.secondary" gutterBottom>
                            Settings
                        </Typography>
                    </CardContent>
                </Card>
            }
            {cardOpen && 
                <Card className={classnames(classes.card, classes.cardOpen)}>
                    <CardActionArea onClick={(event) => handleCloseCard(event)} className={responseLoading ? classes.cardLoading : ""}>
                        <CardMedia
                            component="img"
                            height="300"
                            image={datasetSelected ? previewImage : require("../../assets/objectDetection/images/preview.jpg")}
                            alt="Dataset not selected"
                        />
                        <CardContent className={classes.cardContent}>
                            {datasetSelected &&
                                <Box>
                                    <Typography sx={{ fontSize: 15 }} color="text.secondary" gutterBottom>
                                        Dataset Active:
                                    </Typography>
                                    <Typography variant="h3" component="div">
                                        {datasetSelected.name}
                                    </Typography>
                                </Box>
                            }
                        </CardContent>
                    </CardActionArea>
                </Card>
            }
        </Box>
    )
}

export default BasicCard;
