import { makeStyles } from "@mui/styles";
import Card from '@mui/material/Card';
import { useState } from "react";
import { Box, CardActionArea, CardContent, CardMedia, Typography } from "@mui/material";
import classnames from "classnames";
import SettingsIcon from '@mui/icons-material/Settings';
import { MontecarloPokerSimulationSettings } from "../../models/types";

const useStyles = makeStyles({
    cardBox:{
        position: "absolute",
        zIndex: 1000,
        top: 30,
        right: 30,
    },
    card: {
        cursor: "pointer",
        minWidth: 200,
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        border: "1px solid #f5f5f5",
        boxShadow: "-4px 4px 4px -4px grey !important",
        "&:hover":{
            backgroundColor: "#f3f3f3",
        },
        "& > .MuiCardActions-root":{
            minWidth: 200,
        },
        "& > .MuiCardContent-root": {
            display: "flex",
            gap: 10,
            alignItems: "center",
            justifyItems: "center",
            padding: 0
        }
    },
    cardContent: {
    },
    cardActions:{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: 15
    },
    cardDetails:{
        display: "flex",
        flexDirection: "column",
        alignItems: "flex-start",
        width: "85%",
        gap: 10
    },
    cardOpen:{
        cursor: "auto",
        minWidth: 250,
        boxShadow: "-2px 2px 2px -2px grey !important",
        "&:hover":{
            backgroundColor: "white",
            "& > div > p, & > div > div":{
                color: "text.primary !important"
            }
        }
    },
})

interface CardProps{
    setSettings: Function
}

const BasicCard = (props: CardProps) => {
    const classes = useStyles();

    const [cardOpen, setCardOpen] = useState(false);
    const [expandDatasetDetails, setExpandDatasetDetails] = useState(false);
    const [settings, setSettings] = useState<MontecarloPokerSimulationSettings>({nPlayers: 1, nSamples: 10000});

    const handleClose = () => {
        props.setSettings()
    };

    const handleCloseCard = (e:any) => {
        setCardOpen(!cardOpen)
    }

    const handleExpandClick = () => {
        setExpandDatasetDetails(!expandDatasetDetails);
    };


    return (
        <Box className={classes.cardBox}>
            {!cardOpen &&
                <Card className={classnames(classes.card, cardOpen ? classes.cardOpen : "")} onClick={(event) => handleCloseCard(event)}>
                    <CardContent>
                        <SettingsIcon />  
                        <Typography sx={{ fontSize: 15, margin: 0 }} color="text.secondary" gutterBottom>
                            Settings
                        </Typography>
                    </CardContent>
                </Card>
            }
            {cardOpen && 
                <Card className={classnames(classes.card, classes.cardOpen)}>
                    <CardActionArea onClick={handleCloseCard}>
                        <CardContent className={classes.cardContent}>
                            Set samples and players<br/>
                            nSamples: {settings.nSamples}<br/>
                            nPlayers: {settings.nPlayers}
                        </CardContent>
                    </CardActionArea>
                </Card>
            }
        </Box>
    )
}

export default BasicCard;
