import { useState } from "react";
import { makeStyles } from "@mui/styles";
import { Box, Typography } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import { Ranking, Suit } from "../../models/types";

const useStyles = makeStyles({
    box: {
        width: 70,
        height: 150,
        border: "1px solid black",
    },
});

interface PlayingCardProps {
    suit: Suit;
    ranking: Ranking;
}

const PlayingCard = (props: PlayingCardProps) => {
    const classes = useStyles();
    const suit = props.suit;
    const ranking = props.ranking;

    const showCard = () => {
        let color = "";
        let suitNode = undefined;
        switch(suit.value){
            case "H":
                color = "red";
                suitNode = <FavoriteIcon htmlColor={color}/>
            case "D":
                color = "red";
                suitNode = <FavoriteIcon htmlColor={color}/>
            case "D":
                color = "black";
                suitNode = <FavoriteIcon htmlColor={color}/>
            case "D":
                color = "black";
                suitNode = <FavoriteIcon htmlColor={color}/>
            default:
                color = "red";
                suitNode = <FavoriteIcon htmlColor={color}/>
        }
        let rankingNode = <span style={{color: color, fontWeight: "bold"}}>{ranking.value}</span>
        return <Box>
            {rankingNode}{suitNode}
        </Box>
    }

    return (
        <Box role="playing-card" className={classes.box}>
            {showCard()}
        </Box>
    );
}

export default PlayingCard;
