import { useState } from "react";
import { makeStyles } from "@mui/styles";
import { Box, Typography } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import { Ranking, Suit } from "../../models/types";

const useStyles = makeStyles({
    box: {
        width: 70,
        height: 150,
        border: "1px solid black",
    },
});

interface PlayingCardProps {
    suit: Suit;
    ranking: Ranking;
}

const PlayingCard = (props: PlayingCardProps) => {
    const classes = useStyles();
    const suit = props.suit;
    const ranking = props.ranking;

    const getSuit = () => {
        switch(suit.label){
            case "H":
                return <FavoriteIcon htmlColor="red"/>
            case "D":
                return <FavoriteIcon htmlColor="red"/>
            case "D":
                return <FavoriteIcon htmlColor="black"/>
            case "D":
                return <FavoriteIcon htmlColor="black"/>
        }
    }

    return (
        <Box role="playing-card" className={classes.box}>
            {getSuit()}
        </Box>
    );
}

export default PlayingCard;
